package StringDemo;

import java.util.Scanner;

public class Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc=new Scanner(System.in);
		String str1=sc.next();
		String Str2=sc.nextLine();
		String str3=Str2+str1;
		System.out.println(str3);

	}

}
